(function ($){
    var $lightboxSwitchElements,
        $lightboxContainer,
        $lightboxBody;

    function switchLightbox (){
        $lightboxContainer.toggle('.display-none');
    }

    function boxStopPropagation(event){
        event.stopPropagation();
    }

    function bindings() {
        $lightboxSwitchElements.on('click', switchLightbox);
        $lightboxBody.on('click', boxStopPropagation);
    }

    $(document).ready(function(){
        $lightboxSwitchElements = $('#lightbox-btn, #lightbox'),
        $lightboxContainer = $('#lightbox'),
        $lightboxBody = $('.box');

        bindings();
    });
})(jQuery);
